import React, { useEffect, useState } from 'react'
import Home from './components/HomePage/Home'
import PostsPage from './components/PostsPage/PostsPage'
import LoginPage from './components/LoginPage/LoginPage';
import PostPage from './components/PostPage/PostPage';
import ProfilePage from './components/ProfilePgae/ProfilePage';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Routes, Route, Link } from "react-router-dom";
import { LoginContext } from './contexts/LoginContext';
import { UserIdContext } from './contexts/UserIdContext';
import PostsOutlet from './components/PostsOutlet/PostsOutlet';

const App = () => {
  const [login, setLogin] = useState(false);
  const [userId, setUserId] = useState("3");

  const logout = () => {
    setLogin(false);
    sessionStorage.clear();
  };

  sessionStorage.clear();

  return (
    <div className='container'>
      <BrowserRouter>
        <LoginContext.Provider value={{ login, setLogin }}>
          <UserIdContext.Provider value={{ userId, setUserId }}>
            <ul className="nav mb-3">
              <li className="nav-item">
                <Link className="nav-link" to="/">Home</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="posts">Posts</Link>
              </li>
              <li className="nav-item">
                <Link className="nav-link" to="profile">Profile</Link>
              </li>
              {/* <li className="nav-item">
                <Link className="nav-link" to="login" id='login'>Login</Link>
              </li> */}
              {" "}
              {!login && (
                <Link className="nav-link" to="login">
                  Login
                </Link>
              )}
              {login && (
                <button className="nav-link" onClick={logout}>
                  Logout
                </button>
              )}
            </ul>
            <hr />
            <Routes>
              <Route path="/" element={<Home />}>
              </Route>
              <Route path="/posts" element={<PostsOutlet />}>
                <Route index element={<PostsPage />} />
                <Route path=":id" element={<PostPage />} />
              </Route>
              <Route path="/profile" element={<ProfilePage />}>
              </Route>
              <Route path="/login" element={<LoginPage />}>
              </Route>
            </Routes>
          </UserIdContext.Provider>
        </LoginContext.Provider>
      </BrowserRouter>
    </div>
  )
}

export default App
