import React, { useEffect, useState } from 'react'
import Home from './components/HomePage/Home'
import PostsPage from './components/PostsPage/PostsPage'
import LoginPage from './components/LoginPage/LoginPage';
import PostPage from './components/PostPage/PostPage';
import ProfilePage from './components/ProfilePgae/ProfilePage';
import 'bootstrap/dist/css/bootstrap.min.css';
import { BrowserRouter, Routes, Route, Link, useNavigate } from "react-router-dom";
import PostsOutlet from './components/PostsOutlet/PostsOutlet';
import { Button } from 'bootstrap';

const App = () => {
  const [token, setToken] = useState(JSON.parse(localStorage.getItem('token') || ''));

  useEffect(() => {
    if (token) {
      setToken(JSON.parse(localStorage.getItem('token') || ''))
    } else {
      handleGoLogin()
    }
  }, [token])

  const handleDeleteToken = () => {
    localStorage.setItem('token', '')

  }

  const handleGoLogin = () => {
    const getFileElm = document.getElementById(`login`);
    getFileElm.click();
  };

  return (
    <div className='container'>
      <BrowserRouter>
        <ul className="nav mb-3">
          <li className="nav-item">
            <Link className="nav-link" to="/">Home</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="posts">Posts</Link>
          </li>
          <li className="nav-item">
            <Link className="nav-link" to="profile">Profile</Link>
          </li>
          <li className="nav-item">
            {token ? <button onClick={handleDeleteToken}>Logout</button> : <Link className="nav-link" to="login" id='login'>Login</Link>}
          </li>
        </ul>
        <hr />
        <Routes>
          <Route path="/" element={<Home />}>
          </Route>
          <Route path="/posts" element={<PostsOutlet />}>
            <Route index element={<PostsPage />} />
            <Route path=":id" element={<PostPage />} />
          </Route>
          <Route path="/profile" element={<ProfilePage />}>
          </Route>
          <Route path="/login" element={<LoginPage />}>
          </Route>
        </Routes>
      </BrowserRouter>
    </div>
  )
}

export default App
