import React, { useState, useEffect, useContext } from 'react'
import axios from 'axios';
import { LoginContext } from '../../contexts/LoginContext';
import { UserIdContext } from '../../contexts/UserIdContext';
import { setTokens, getTokens } from './LoginHelper';

const validateEmail = email => {
    if (!email) return "Email is required";
    const validEmail = String(email).toLowerCase()
        .match(
            /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/
        );
    if (!validEmail) {
        return "Email is invalid"
    }
}

const validatePassword = password => {
    if (!password) return "Password is required";
    if (password.length < 8) return "At least 8 characters";
}

const LoginPage = (props) => {
    //localStorage.removeItem('token')

    const [values, setValues] = useState({
        email: "",
        password: ""
    });
    const [touched, setTouched] = useState({
        email: false,
        password: false
    })

    const { login, setLogin } = useContext(LoginContext);
    const { userId, setUserId } = useContext(UserIdContext);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");

    // useEffect(() => {
    //     let didCancel = false;
    //     axios({
    //         method: "GET",
    //         url: `https://60dff0ba6b689e001788c858.mockapi.io/tokens`,
    //     })
    //         .then(({ data }) => {
    //             if (!didCancel && login) {
    //                 console.log("dâta", data)
    //                 //axios.defaults.headers.common['Authorization'] = data.token;
    //                 setTokens(data);
    //                 console.log("first");
    //             }
    //             setLoading(false);
    //         })
    //         .catch((err) => {
    //             if (!didCancel) {
    //                 setLoading(false);
    //                 setError("Something went Wrong");
    //             }
    //         });

    //     return () => {
    //         didCancel = true;
    //     };
    // }, [login]);

    if (loading) return <h1 style={{ textAlign: "center" }}>Loading....</h1>;
    if (error) return <p style={{ color: "red" }}> {error}</p>;
    console.log(getTokens());

    const errorEmail = validateEmail(values.email);
    const errorPassword = validatePassword(values.password);

    const handleChange = (evt) => {
        setValues({
            ...values,
            [evt.target.name]: evt.target.value
        })
    }
    const handleOnBlur = evt => {
        setTouched({
            ...touched,
            [evt.target.name]: true
        })
    }

    const handleSubmit = (evt) => {
        if (errorEmail && errorPassword) return;
        evt.preventDefault();
        setLogin({ login: true });
    };

    if (login) {
        const tokens = getTokens();
        if (tokens) {
            setUserId(tokens.userId);
        }
    }

    return (
        <div className='row'>
            <div className='col-3'></div>
            <form className='col-6' onSubmit={handleSubmit}>
                <h3 className='text-center'>Login</h3>
                <div className='mb-3'>
                    <input
                        className="form-control"
                        type="text"
                        placeholder='Email'
                        value={values.email}
                        onChange={handleChange}
                        onBlur={handleOnBlur}
                        name='email' />
                    {(touched.email) && <div className="form-text text-danger">{errorEmail}</div>}
                </div>
                <div className='mb-3'>
                    <input
                        className="form-control"
                        type="password"
                        placeholder='Password'
                        value={values.password}
                        onChange={handleChange}
                        onBlur={handleOnBlur}
                        name='password' />
                    {(touched.password) && <div className="form-text text-danger">{errorPassword}</div>}
                </div>
                <button className="btn btn-primary col-12" type='submit'>Submit</button>
                {login && <div className='text-success'><b>Login success</b></div>}
            </form>
            <div className='col-3'></div>
        </div>
    )
}

export default LoginPage
